//in Reacht environment if you import a css file, it is applie
//to the component when rendeed..
import React, {Component} from 'react';
import '../css/Board.css';
import Note from './Note';

//ECMASCRIPT Javascript Class...
class Board extends Component {
    constructor() {
        super();
        this.state = {
            notes: [
                {
                    title: "Class Notes",
                    body: "Always use constructors when extending another class",
                },
                {
                    title: "My New Address",
                    body: "6275 Souder Street, Brooklyn, New York"
                },
                {
                    title:"React Notes",
                    body: "Everything in React is a component"
                }
            ]
        };
    }

    addNote() {
        let notes = this.state.notes;
        notes.push(
            {
            title: "New Note Title",
            body: "New Note Body"
            }
        );
        this.setState(
            {
                notes: this.state.notes
            }
        );
    }


    //creates method for the class
    render() {
        return (
            <div>
                <div className="div-board">
                    <div className="row">
                        {
                            this.state.notes
                        }
                    </div>
                </div>
                <div>
                    <button className="btn btn-success add-button">Add</button>
                </div>
            </div>
        )
    }
}

//make this file avaialable in other modules
export default Board;
